import * as React from 'react';
import { View, Text, Pressable, StyleSheet, Button } from 'react-native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { NavigationContainer } from '@react-navigation/native';
import Ionicons from 'react-native-vector-icons/Ionicons';
import UsersScreen from './screens/Contactos';
import PostScreen from './screens/Posts';
import PostsDetail from './screens/PostsDetail';
import Puntos from './screens/Puntos';
import Maps from './screens/Maps';
import Servicios from './screens/Servicios';
import AddServicio from './screens/AddServicio';
import EditServicio from './screens/EditServicio';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import { createDrawerNavigator } from '@react-navigation/drawer';




function Inicio({ navigation }) {
  return (
    <View style={styles.container}>
      <Text style={styles.texto}>PÃ¡gina o componente inicial</Text>
      <Button
        style={styles.boton}
        title="Ir a Servicios"
        onPress={() => navigation.navigate('Servicios')} />
      <Button
        style={styles.boton}
        title="Ir a Puntos"
        onPress={() => navigation.navigate('Puntos')} />
    </View>
  );
}


//const Stack = createNativeStackNavigator();
const Drawer = createDrawerNavigator();
export default function App() {
  return (
    <NavigationContainer>
      <Drawer.Navigator
           screenOptions={({ route }) => ({
            drawerIcon: ({ focused, color, size }) => {
              let iconName;

              if (route.name === 'Inicio') {
                iconName = focused
                  ? 'home-outline'
                  : 'home-outline';
              } else if (route.name === 'Servicios') {
                iconName = focused ? 'list-outline' : 'list-outline';
              } else if (route.name === 'Agregar Servicio') {
                iconName = focused ? 'md-add-circle-outline' : 'md-add-circle-outline';
              }

              // You can return any component that you like here!
              return <Ionicons name={iconName} size={size} color={color} />;
            },
            tabBarActiveTintColor: 'tomato',
            tabBarInactiveTintColor: 'gray',
          })}>
        <Drawer.Screen name="Inicio" component={Inicio} />
        <Drawer.Screen name="Servicios" component={Servicios} />
        <Drawer.Screen name="Agregar Servicio" component={AddServicio} />
        <Drawer.Screen name="Contactos" component={UsersScreen} />
        <Drawer.Screen name="Puntos" component={Puntos} />
        <Drawer.Screen name="EditServicio" component={EditServicio} />
      </Drawer.Navigator>

    </NavigationContainer>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    //backgroundColor: '#cdcd',
    alignItems: 'center',
    justifyContent: 'center',
  },
  boton: {
    width: 80,
    height: 40,
    alignItems: 'center',
    justifyContent: 'center',
    marginBottom: 10,
    borderRadius: 50,

  },
  texto: {
    fontSize: 15,
    color: 'green',
    marginBottom: 15,

  },
});
