import React, { useState, useEffect } from 'react';
import { Box, Heading, AspectRatio, Image, Text, Center, HStack, Stack, NativeBaseProvider, } from "native-base"
import { StyleSheet, TouchableHighlight, View, Dimensions, ScrollView } from "react-native";

const { width, height } = Dimensions.get('window');





export function Acercade({ navigation }) {
    const [datos, setDatos] = useState([])
    const fetchUser = async () => {
        const response = await fetch('https://www.sit-zac.org.mx/getContenidoApp')
        const data = await response.json()
        //console.log(data.data);
        setDatos(data.data);
    }

    useEffect(() => {
        fetchUser();
    }, [])

    return (
        <NativeBaseProvider>
            <View style={styles.container}>            
                <TouchableHighlight
                    activeOpacity={0.6}
                    underlayColor="#DDDDDD"
                    onPress={() => { navigation.navigate('Acerca') }}
                >
                    <Center flex={1} px="3">
                        <Box
                            maxW="80"
                            rounded="lg"
                            overflow="hidden"
                            borderColor="coolGray.200"
                            borderWidth="1"
                            _dark={{
                                borderColor: "coolGray.600",
                                backgroundColor: "gray.700",
                            }}
                            _web={{
                                shadow: 2,
                                borderWidth: 0,
                            }}
                            _light={{
                                backgroundColor: "gray.50",
                            }}
                        >
                            <Box>
                                <AspectRatio w="100%" ratio={16 / 9}>
                                    <Image
                                        source={{
                                            uri: "https://www.sit-zac.org.mx/welcome_template/assets/img/why-us.png",
                                        }}
                                        alt="image"
                                    />
                                </AspectRatio>
                                <Center
                                    bg="blue.500"
                                    _dark={{
                                        bg: "blue.400",
                                    }}
                                    _text={{
                                        color: "warmGray.50",
                                        fontWeight: "700",
                                        fontSize: "xs",
                                    }}
                                    position="absolute"
                                    bottom="0"
                                    px="3"
                                    py="1.5"
                                >
                                    {datos.titulo_acerca_de}
                                </Center>
                            </Box>
                            <Stack p="4" space={3}>
                                <Stack space={2}>
                                    <Heading size="md" ml="-1">
                                        Tribunal de Justicia Administrativa
                                    </Heading>
                                    <Text
                                        fontSize="xs"
                                        _light={{
                                            color: "blue.500",
                                        }}
                                        _dark={{
                                            color: "blue.400",
                                        }}
                                        fontWeight="500"
                                        ml="-0.5"
                                        mt="-1"
                                    >
                                        Guadalupe, Zacatecas.
                                    </Text>
                                </Stack>
                                <Text fontWeight="400">
                                    {datos.desc_acerca_de} Leer mas...
                                </Text>
                                <HStack alignItems="center" space={4} justifyContent="space-between">
                                    <HStack alignItems="center">
                                        <Text
                                            isTruncated='true'
                                            color="coolGray.600"
                                            _dark={{
                                                color: "warmGray.200",
                                            }}
                                            fontWeight="400"
                                        >
                                            {datos.updated_at}
                                        </Text>
                                    </HStack>
                                </HStack>
                            </Stack>
                        </Box>
                    </Center>
                </TouchableHighlight>
                <TouchableHighlight
                    activeOpacity={0.6}
                    underlayColor="#DDDDDD"
                    onPress={() => { navigation.navigate('JuicioLinea') }}
                >
                    <Center flex={1} px="3">
                        <Box
                            maxW="80"
                            rounded="lg"
                            overflow="hidden"
                            borderColor="coolGray.200"
                            borderWidth="1"
                            _dark={{
                                borderColor: "coolGray.600",
                                backgroundColor: "gray.700",
                            }}
                            _web={{
                                shadow: 2,
                                borderWidth: 0,
                            }}
                            _light={{
                                backgroundColor: "gray.50",
                            }}
                        >
                            <Box>
                                <AspectRatio w="100%" ratio={16 / 9}>
                                    <Image
                                        source={{
                                            uri: "https://www.sit-zac.org.mx/welcome_template/assets/img/skills.png",
                                        }}
                                        alt="image"
                                    />
                                </AspectRatio>
                                <Center
                                    bg="blue.500"
                                    _dark={{
                                        bg: "blue.400",
                                    }}
                                    _text={{
                                        color: "warmGray.50",
                                        fontWeight: "700",
                                        fontSize: "xs",
                                    }}
                                    position="absolute"
                                    bottom="0"
                                    px="3"
                                    py="1.5"
                                >
                                    {datos.titulo_juicio_linea}
                                </Center>
                            </Box>
                            <Stack p="4" space={3}>
                                <Stack space={2}>
                                    <Heading size="md" ml="-1">
                                        Juicio en Línea
                                    </Heading>
                                    <Text
                                        fontSize="xs"
                                        _light={{
                                            color: "blue.500",
                                        }}
                                        _dark={{
                                            color: "blue.400",
                                        }}
                                        fontWeight="500"
                                        ml="-0.5"
                                        mt="-1"
                                    >
                                        Guadalupe, Zacatecas.
                                    </Text>
                                </Stack>
                                <Text fontWeight="400">
                                    {datos.desc_juicio_linea} Leer mas...
                                </Text>
                                <HStack alignItems="center" space={4} justifyContent="space-between">
                                    <HStack alignItems="center">
                                        <Text
                                            isTruncated='true'
                                            color="coolGray.600"
                                            _dark={{
                                                color: "warmGray.200",
                                            }}
                                            fontWeight="400"
                                        >
                                            {datos.updated_at}
                                        </Text>
                                    </HStack>
                                </HStack>
                            </Stack>
                        </Box>
                    </Center>

                </TouchableHighlight>
            </View>
        </NativeBaseProvider>

    )
}

export default function Acerca({ navigation }) {
    return (
        <ScrollView horizontal={true} style={styles.scroll}>
            <Acercade navigation={navigation} />
        </ScrollView>
    )
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
        flexDirection: 'row',
        borderLeftWidth: 60,
        borderTopWidth: 0,
    },
    scroll: {
        width: Dimensions.get('window').width + 100,
    },
});
