import React, { useState, useEffect } from 'react'
import { View } from "react-native"
import Carousel from 'react-native-snap-carousel'
import CarouselCardItem, { SLIDER_WIDTH, ITEM_WIDTH } from '../src/Home/CarouselCardItem'
import { Text, StyleSheet, TextInput, Dimensions, Button, ScrollView, ActivityIndicator, ImageBackground } from 'react-native';
import { Center, NativeBaseProvider, StatusBar, Box } from "native-base"
import Acerca from '../src/Home/Acerca';
import Header from '../components/Header';

const { width, height } = Dimensions.get('window');



export default function ListHome({ navigation }) {
    console.log(navigation);

    const [cargando, setCargando] = useState(true)
    const [noticias, setNoticias] = useState([])

    const fetchUser = async () => {
        const response = await fetch('https://www.sit-zac.org.mx/getNoticias')
        const data = await response.json()
        //  console.log(data.avisos);
        setNoticias(data.avisos)
        setCargando(false)
    }


    useEffect(() => {
        fetchUser();
    }, [])



    const isCarousel = React.useRef(null)
    if (cargando) {
        return (
            <View>
                <Text>Cargando Avisos...</Text>
                <ActivityIndicator size="large" color="#0000f" />
            </View>
        )
    } else {
        return (

            <View style={styles.container}>
                <ImageBackground source={require("../assets/fondo1.png")} resizeMode="cover" style={styles.image}>
                 
                    <ScrollView style={styles.scroll}>
                        <Text style={styles.titulo}>Últimos avisos</Text>
                        <Header />
                        <Carousel
                            layout="default"
                            layoutCardOffset={9}
                            ref={isCarousel}
                            data={noticias}
                            renderItem={CarouselCardItem}
                            sliderWidth={SLIDER_WIDTH}
                            itemWidth={ITEM_WIDTH}
                            inactiveSlideShift={0}
                            useScrollView={true}

                        />
                        <Header />
                        <NativeBaseProvider>
                        <Center flex={1} px="3">
                            <Box
                                bg="primary.500"
                                _text={{
                                    fontSize: "md",
                                    fontWeight: "medium",
                                    color: "warmGray.50",
                                    letterSpacing: "lg",
                                }}
                            >
                               Conoce más del Tribunal
                            </Box>                         
                        </Center>
                    </NativeBaseProvider>
                        <Text style={styles.titulo}>Conoce más del Tribunal</Text>
                        <Acerca navigation={navigation} />
                        <StatusBar style="auto" />

                    </ScrollView>
                </ImageBackground>
            </View>
        )
    }
}



const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center',
    },
    scroll: {
        width: Dimensions.get('window').width + 120,

    },
    image: {
        flex: 1,
        justifyContent: "center"
    },
    titulo: {
        fontSize: 22,
        height: 40,
        borderBottomColor: '#ccc',
        borderBottomWidth: 1,
        color: '#rgb(151, 132 , 102)',
        fontWeight: "bold",
        alignSelf: 'center',
        fontFamily: "sans-serif",
    },
});
