import React, { useState, useEffect } from 'react';
import { Text, View, ActivityIndicator, StyleSheet, Dimensions, ScrollView, Image } from 'react-native';
const GET_WELCOME = 'GET_WELCOME';
const { width, height } = Dimensions.get('window');

async function fetchData() {
  const response = await fetch('https://www.sit-zac.org.mx/getContenidoApp');
  const json = await response.json();
  // console.log(json);
  return json;
}



const HelloWorldApp = () => {
  const [datos, setDatos] = useState([])
  const [cargando, setCargando] = useState(true)


  async function traer_Datos() {
    const response = await fetch('https://www.sit-zac.org.mx/getContenidoApp');
    const json = await response.json();
    setDatos(json.data);
    setCargando(false);
  }

  useEffect(() => {
    traer_Datos();
  }, [])


  if (cargando) {
    return (
      <View>
        <Text>Cargando datos...</Text>
        <ActivityIndicator size="large" color="#0000f" />
      </View>
    )
  } else {
    return (
      <View style={styles.container}>
        <ScrollView style={styles.scroll}>
          <Text style={styles.titulo1}>{datos.titulo_acerca_de}</Text>
          <View style={styles.body}>
            <Image
              style={styles.tinyLogo}
              source={{ uri: 'https://www.sit-zac.org.mx/welcome_template/assets/img/zacatecas.jpg' }}
            />
            <Text style={styles.titulo2}>{datos.acerca_de}</Text>
          </View>
        </ScrollView >
      </View>
    );
  }

}


export default HelloWorldApp;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: '#f3f5fa',
  },
  body: {
    alignItems: 'center',
    justifyContent: 'center',
    marginTop: 20,
  },
  input: {
    height: 40,
    backgroundColor: '#dfdf',
    width: width,

  },
  titulo2: {
    fontSize: 18,
    color: '#343a40',
    textAlign: 'justify',
    letterSpacing: 1,
    margin: 10,

  },
  scroll: {
    width: Dimensions.get('window').width,

  },
  titulo1: {
    fontSize: 22,
    height: 40,
    borderBottomColor: '#ccc',
    borderBottomWidth: 1,
    color: '#rgb(151, 132 , 102)',
    fontWeight: "bold",
    alignSelf: 'center',
    fontFamily: "sans-serif",
  },
  tinyLogo: {
    width: 200,
    height: 200,
    alignSelf: 'center',
    borderRadius: 100,
    borderTopWidth: 20
  },
});
